import fetch from 'node-fetch'
import { type ZodRawShape, z } from 'zod'
import { commons_SheetIdSchema } from '../schemas.js'
import type { Tool } from './index.js'

const params = {
  sheetId: commons_SheetIdSchema.describe('The associated sheet ID of the view.'),
  pageSize: z.number().int().optional().describe('Number of prompts to return in a page (default 10)'),
  pageNumber: z.number().int().optional().describe('Based on pageSize, which page of prompts to return'),
} as ZodRawShape

export const listViews: Tool<typeof params> = {
  name: 'listViews',
  description: 'List views by Sheet: Returns all views for the sheet',
  params,
  cb: async ({ sheetId, pageSize, pageNumber }) => {
    try {
      const searchParams = { sheetId, pageSize, pageNumber }

      const searchParamsString = new URLSearchParams(JSON.parse(JSON.stringify(searchParams))).toString()
      const baseUrl = process.env.FLATFILE_API_URL || 'https://platform.flatfile.com/api/v1'
      const url = `${baseUrl}/views${searchParamsString ? `?${searchParamsString}` : ''}`

      const response = await fetch(url, {
        method: 'GET',
        headers: {
          'X-Disable-Hooks': 'true',
          Authorization: `Bearer ${process.env.FLATFILE_BEARER_TOKEN}`,
          'Content-Type': 'application/json',
        },
      })

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status} - ${response.statusText}`)
      }

      const data = (await response.json()) as { data: unknown }

      return {
        content: [
          {
            type: 'text',
            text: JSON.stringify(data.data, null, 2),
          },
        ],
        status: 'success',
      }
    } catch (error) {
      return {
        content: [{ type: 'text', text: `Error: ${error}` }],
        status: 'failed',
      }
    }
  },
}
